INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (5, 5, 5, 4, '接客も料理もとても丁寧で、気持ちよく食事を楽しめました。また来たいです。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (6, 6, 6, 3, '接客は、とても丁寧で親切でした。料理は、どれも美味しく、また食べたいと思いました。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (7, 7, 7, 5, 'ランチのセットはとてもお得で、ボリュームもたっぷりでした。料理は、どれも美味しく、また利用したいと思いました。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (8, 8, 8, 5, '料理は、どれも美味しく、また食べたいと思いました。ただ、もう少し値段が安ければ、より利用しやすいと思います。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (9, 9, 9, 4, '店内がとてもきれいで、清潔感がありました。また来たいです。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (10, 10, 10, 3, '料理も接客も申し分ありませんでした。大満足です。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (11, 11, 11, 2, '料理は美味しいけど、もう少しメニューの種類があると嬉しいです。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (12, 12, 12, 3, '店員さんがとても親切で、気持ちよく食事を楽しめました。また来たいです。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (13, 13, 13, 5, '料理はどれも新鮮で、素材の味が活きていました。また来たいです。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (14, 14, 14, 5, '店員さんの対応がとても丁寧で、気持ちよく食事を楽しめました。また来たいです。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (15, 15, 15, 4, '雰囲気がとても落ち着いていて、ゆったりと食事を楽しめました。また来たいです。');
INSERT IGNORE INTO reviews (id, member_id, restaurant_id, score, content) VALUES (16, 16, 16, 3, '値段は少し高めですが、それだけの価値はあると思います。また来たいです。');
